mapper.select:
SELECT yyyy, seq, req_date, res_date, res_pl_bid_amt, esti_opi
FROM es01
WHERE yyyy = #{년도, jdbcType=VARCHAR}  AND seq = #{감정순번, jdbcType=VARCHAR}

SELECT TRIM (LPAD (' ', 26, '0')) AS doc_key, NULL AS appr_datetime, NULL AS esti_date, '대상자료없음' AS doc_name, NULL AS jum_code, NULL AS appr_sabun, NULL AS proc_div, NULL AS yyyy, NULL AS seq, NULL AS NAME FROM DUAL UNION SELECT doc_key, appr_datetime, esti_date, doc_name, jum_code, appr_sabun, proc_div, yyyy, seq, NAME FROM (SELECT doc_key, appr_datetime, esti_date, doc_name, jum_code, appr_sabun, proc_div, yyyy, seq, RANK () OVER (PARTITION BY doc_key ORDER BY doc_key, appr_datetime) rnk FROM es05) a, v_ia01 b WHERE a.appr_sabun = b.sabun AND rnk = 1 -- AND jum_code = #{_부서코드}
AND seq IS NULL
ORDER BY esti_date desc

